mapper.select:
SELECT *
FROM `order`


        AND user_id = #{userId}


        AND order_status = #{orderStatus}

mapper.update:
UPDATE `order`
order_status = #{orderStatus}, payment_status = #{paymentStatus}, payment_time = #{paymentTime}, order_total = #{orderTotal}, shipping_status = #{shippingStatus}, shipping_time = #{shippingTime}, update_time = NOW()
WHERE order_id = #{orderId}
